template.value:
namespace GiftogramCommon\Sidecar;

use Hammerstone\Sidecar\LambdaFunction;
use Hammerstone\Sidecar\Sidecar as Hammerside;

class  $NAME$ extends LambdaFunction {
    public function layers(): array
    {
        // leave blank if you have no layers
        return [];
    }

    public function variables(): array
    {
        // leave blank if you have no layers
        return [];
    }

    public function handler(): string
    {
        return '$handlerDir$/handler@handler';
    }

    public function package(): array
    {
        // JS files to be uploaded
        return [
            $END$
        ];
    }

    /**
    * NEVER TOUCH THIS
    **/
    public function prefix(): string
    {
        // Don't forget the environment!
        return 'SC-Giftogram-' . Hammerside::getEnvironment() . '-';
    }
}
